	bool MeshGenerate::isPointInLine(const MyPoint& pt,const MyPoint& linePtA, const MyPoint& linePtB)
	{
		if (numbers::isEqual( (pt-linePtA).norm()+(pt-linePtB).norm(),(linePtA-linePtB).norm() ))
		{
			return true;
		}
		else
		{
			return false;
		}
	}

	MyDenseVector MeshGenerate::computeSurfaceNormal(const MyPoint& A,const MyPoint& B,const MyPoint& C)
	{
		return (A-B).cross(B-C);
	}

	void MeshGenerate::testBladeValid(MyFloat ptA0,MyFloat ptA1,MyFloat ptA2,MyFloat ptB0,MyFloat ptB1,MyFloat ptB2,MyFloat ptC0,MyFloat ptC1,MyFloat ptC2)
	{
		MyDenseVector pA(ptA0,ptA1,ptA2),pB(ptB0, ptB1, ptB2),pC(ptC0, ptC1, ptC2);
		MyDenseVector lines[3][2] = {{pA,pB},{pB,pC},{pC,pA}};
		const unsigned nVertexSize = m_nMaxVertexCount;
		for (unsigned v=0;v<nVertexSize;++v)
		{
			if (m_vec_MCVertex[v].m_isValid)
			{
				const MyDenseVector& curPos = m_vec_MCVertex[v].m_VertexPos;
				if (isPointInLine(curPos,lines[0][0],lines[0][1]))
				{
					printf("testBladeValid error!\n");
					MyPause;
				}
				if (isPointInLine(curPos,lines[1][0],lines[1][1]))
				{
					printf("testBladeValid error!\n");
					MyPause;
				}
				if (isPointInLine(curPos,lines[2][0],lines[2][1]))
				{
					printf("testBladeValid error!\n");
					MyPause;
				}

				if (isPointInTri(curPos,pA,pB,pC))
				{
					printf("isPointInTri point in tri.\n");
					MyPause;
				}
			}
		}
		
	}

