	MyFloat MeshGenerate::computeTriangleArea(const MyPoint& A,const MyPoint& B,const MyPoint& C)
	{
		MyFloat a = (A-B).norm();
		MyFloat b = (B-C).norm();
		MyFloat c = (C-A).norm();
		MyFloat s = (a + b + c) / 2.0; 
		return sqrt(s * (s - a) * (s - b) * (s - c)); 
	}

	bool MeshGenerate::isPointInTri(const MyPoint& pt,const MyPoint& TriPtA, const MyPoint& TriPtB, const MyPoint& TriPtC)
	{
		MyFloat S = computeTriangleArea(TriPtA,TriPtB,TriPtC);
		MyFloat S0 =computeTriangleArea(pt,TriPtA,TriPtB);
		MyFloat S1 =computeTriangleArea(pt,TriPtC,TriPtB);
		MyFloat S2 =computeTriangleArea(pt,TriPtC,TriPtA);

		return numbers::isEqual(S,S0+S1+S2);
	}

	MyFloat MeshGenerate::checkPointPlane(const MyPoint& pt,const MyPoint& A,const MyPoint& B,const MyPoint& C,const MyDenseVector& normal)const
	{
		MyDenseVector tmpVec = (pt-A);
		return normal[0]*tmpVec[0] + normal[1]*tmpVec[1] + normal[2]*tmpVec[2];
	}

	int MeshGenerate::computePt2Blade(const MyPoint& pt,const MyPoint& A,const MyPoint& B,const MyPoint& C)
	{
		/*if (isPointInTri(pt,A,B,C))
		{
			printf("computePt2Blade == 0\n");
			MyPause;
			return 0;
		}*/
		
		MyFloat val = checkPointPlane(pt,A,B,C,computeSurfaceNormal(A,B,C));
		if (val > 0)
		{
			return 1;
		}
		else if (val < 0)
		{
			return -1;
		}
		else
		{
			printf("computePt2Blade error!\n");
			MyPause;
		}
	}

	MyFloat MeshGenerate::computePt2LineDistance(const MyPoint& pt,const MyPoint& linePtA, const MyPoint& linePtB)
	{
		return ((pt - linePtA).dot(linePtB - linePtA)) / (linePtB - linePtA).norm();
	}

	bool MeshGenerate::CheckLineTri( const MyPoint &L1, const MyPoint &L2, const MyPoint &PV1, const MyPoint &PV2, const MyPoint &PV3, MyPoint &HitP )
	{
		MyPoint VIntersect;

		// Find Triangle Normal, would be quicker to have these computed already
		MyPoint VNorm;
		VNorm = ( PV2 - PV1 ).cross( PV3 - PV1 );
		//printf("tmpVec3(%f,%f,%f)\n",VNorm.x,VNorm.y,VNorm.z);
		VNorm.normalize();
		//printf("VNorm(%f,%f,%f)\n",VNorm.x,VNorm.y,VNorm.z);

		// Find distance from L1 and L2 to the plane defined by the triangle
		float fDst1 = (L1-PV1).dot( VNorm );
		//printf("fDst1 is %f \n",fDst1);
		float fDst2 = (L2-PV1).dot( VNorm );
		//printf("fDst2 is %f \n",fDst2);

		if ( (fDst1 * fDst2) >= 0.0f) return false;  // line doesn't cross the triangle.
		if ( fDst1 == fDst2) {return false;} // line and plane are parallel

		// Find point on the line that intersects with the plane
		VIntersect = L1 + (L2-L1) * ( -fDst1/(fDst2-fDst1) );
		//printf("VIntersect(%f,%f,%f)\n",VIntersect.x,VIntersect.y,VIntersect.z);

		// Find if the interesection point lies inside the triangle by testing it against all edges
		MyPoint VTest;
		VTest = VNorm.cross( PV2-PV1 );
		//printf("VTest(%f,%f,%f)\n",VTest.x,VTest.y,VTest.z);
		if ( VTest.dot( VIntersect-PV1 ) < 0.0f ) return false;
		VTest = VNorm.cross( PV3-PV2 );
		//printf("VTest(%f,%f,%f)\n",VTest.x,VTest.y,VTest.z);
		if ( VTest.dot( VIntersect-PV2 ) < 0.0f ) return false;
		VTest = VNorm.cross( PV1-PV3 );
		//printf("VTest(%f,%f,%f)\n",VTest.x,VTest.y,VTest.z);
		if ( VTest.dot( VIntersect-PV1 ) < 0.0f ) return false;

		HitP = VIntersect;

		return true;
	}

	void MeshGenerate::makeVertex2BladeRelationShip(MyFloat ptA0,MyFloat ptA1,MyFloat ptA2,MyFloat ptB0,MyFloat ptB1,MyFloat ptB2,MyFloat ptC0,MyFloat ptC1,MyFloat ptC2)
	{
		MyDenseVector pA(ptA0,ptA1,ptA2),pB(ptB0, ptB1, ptB2),pC(ptC0, ptC1, ptC2);
		//MyDenseVector lines[3][2] = {{pA,pB},{pB,pC},{pC,pA}};
		const unsigned nLineSize = m_nMaxLineCount;
		for (unsigned l=0;l < nLineSize;++l)
		{
			MC_Edge & curEdge = m_vec_MCEdge[l];
			if (curEdge.m_isValid)
			{
				const MyPoint& l0 = m_vec_MCVertex[curEdge.m_Vertex[0]].m_VertexPos;
				const MyPoint& l1 = m_vec_MCVertex[curEdge.m_Vertex[1]].m_VertexPos;
				curEdge.m_isCut = CheckLineTri(l0,l1,pA,pB,pC,curEdge.m_intersectPos);
			}
		}

		m_nLastVertexIdx = m_nMaxVertexCount;
		m_nLastEdgeIdx   = m_nMaxLineCount;
		m_nLastSurfaceIdx= m_nMaxFaceCount;
	}

	void MeshGenerate::makeSplitEdge()
	{
		m_dbDistance = 0.1f;
		MyPoint surfaceSplitPt[2];
		int     surfaceSplitPtInLineIdx[2];
		const unsigned nTriSize = m_nMaxFaceCount;
		for (unsigned t=0;t<nTriSize;++t)
		{
			MC_Surface& curSurface = m_vec_MCSurface[t];

			int nCount = 0;
			int nNoCuttingLineIdx=-1;
			for (unsigned l=0;l<3;++l)
			{
				MC_Edge& curLine = m_vec_MCEdge[curSurface.m_Lines[l]];
				if (curLine.m_isCut)
				{
					surfaceSplitPt[nCount] = curLine.m_intersectPos;
					surfaceSplitPtInLineIdx[nCount] = curSurface.m_Lines[l];
					nCount++;
				}
				else
				{
					nNoCuttingLineIdx = l;
				}
			}

			if (2 == nCount)
			{
				MC_Vertex & curVertex = m_vec_MCVertex[curSurface.m_Vertex[nNoCuttingLineIdx]];
				MyPoint& objPos = curVertex.m_VertexPos;
				MyFloat distance = computePt2LineDistance(objPos,surfaceSplitPt[0],surfaceSplitPt[1]);
				if (distance < m_dbDistance)
				{
					curVertex.m_isSplit = true;
					m_vec_MCEdge[surfaceSplitPtInLineIdx[0]].m_isCut = false;
					m_vec_MCEdge[surfaceSplitPtInLineIdx[1]].m_isCut = false;
				}
			}
		}
	}

	MyPoint MeshGenerate::makeScalarPoint(const MyPoint& src, const MyPoint& dst,MyFloat scalarFactor)
	{
		return src + (dst - src) * scalarFactor;
	}

