	void MeshGenerate::spliteVertex()
	{
		const unsigned nVertexSize = m_nMaxVertexCount;
		for (unsigned v=0;v<nVertexSize;++v)
		{
			MC_Vertex & curVertex = m_vec_MCVertex[v];
			if (curVertex.m_isSplit)
			{
				MC_Vertex clone0,clone1;
				curVertex.m_isValid = false;
				clone0.m_isValid = true;clone0.m_isJoint = true;clone0.m_isSplit = false;
				clone0.m_CloneVertexIdx[0] = clone0.m_CloneVertexIdx[1] = Invalid_Id;
				clone0.m_VertexPos = curVertex.m_VertexPos;
				clone0.m_nVertexId = m_nMaxVertexCount++;
				m_vec_MCVertex[clone0.m_nVertexId] = clone0;
				curVertex.m_CloneVertexIdx[0] = clone0.m_nVertexId;

				clone1.m_isValid = true;clone1.m_isJoint = true;clone1.m_isSplit = false;
				clone1.m_CloneVertexIdx[0] = clone1.m_CloneVertexIdx[1] = Invalid_Id;
				clone1.m_VertexPos = curVertex.m_VertexPos;
				clone1.m_nVertexId = m_nMaxVertexCount++;
				m_vec_MCVertex[clone1.m_nVertexId] = clone1;
				curVertex.m_CloneVertexIdx[1] = clone1.m_nVertexId;
			}
		}
	}

	void MeshGenerate::spliteTriangleByVertex()
	{
		const unsigned nTriSize = m_nMaxFaceCount;
		for (unsigned t=0;t<nTriSize;++t)
		{
			MC_Surface& curSurface = m_vec_MCSurface[t];
			if (curSurface.m_isValid)
			{
				for (unsigned l=0;l<3;++l)
				{
					MC_Edge& curLine = m_vec_MCEdge[curSurface.m_Lines[l]];
					MC_Vertex& p0 = m_vec_MCVertex[curLine.m_Vertex[0]];
					MC_Vertex& p1 = m_vec_MCVertex[curLine.m_Vertex[1]];
					if (p0.m_isSplit)
					{
						//p0.m_isValid = false;
						if (p1.m_state > 0)
						{
							curSurface.m_Vertex[curLine.m_belongToTriVertexIdx[l][0]] = p0.m_CloneVertexIdx[0];
							curLine.m_Vertex[0] = p0.m_CloneVertexIdx[0];
						}
						else if (p1.m_state < 0)
						{
							curSurface.m_Vertex[curLine.m_belongToTriVertexIdx[l][0]] = p0.m_CloneVertexIdx[1];
							curLine.m_Vertex[0] = p0.m_CloneVertexIdx[1];
						}
						else
						{
							printf("error 626\n");
							MyPause;
						}
					}
					else if (p1.m_isSplit)
					{
						//p1.m_isValid = false;
						if (p0.m_state > 0)
						{
							curSurface.m_Vertex[curLine.m_belongToTriVertexIdx[l][1]] = p0.m_CloneVertexIdx[0];
							curLine.m_Vertex[1] = p0.m_CloneVertexIdx[0];
						}
						else if (p0.m_state < 0)
						{
							curSurface.m_Vertex[curLine.m_belongToTriVertexIdx[l][1]] = p0.m_CloneVertexIdx[1];
							curLine.m_Vertex[1] = p0.m_CloneVertexIdx[1];
						}
						else
						{
							printf("error 645\n");
							MyPause;
						}
					}
				}
			}
		}
	}

