	bool VertexInTriangleCircle(MC_Vertex_Cuda& vtx, MC_Surface_Cuda& tri)
	{
		MyFloat dist2 = (vtx.m_VertexPos[0]-tri.m_center[0])*(vtx.m_VertexPos[0]-tri.m_center[0]) + 
						(vtx.m_VertexPos[1]-tri.m_center[1])*(vtx.m_VertexPos[1]-tri.m_center[1]) + 
						(vtx.m_VertexPos[2]-tri.m_center[2]) * (vtx.m_VertexPos[2]-tri.m_center[2]);
		return dist2 < tri.m_R2;
	}

