	MyDenseVector LineLineIntersection(MyDenseVector originD, MyDenseVector directionD, MyDenseVector originE, MyDenseVector directionE) {
		directionD.normalize();
		directionE.normalize();
		MyDenseVector N = directionD.cross(directionE);// Vector3.Cross(directionD, directionE);
		MyDenseVector SR = originD - originE;
		MyFloat absX = std::abs(N[0]);//Math.Abs(N.X);
		MyFloat absY = std::abs(N[1]);//Math.Abs(N.Y);
		MyFloat absZ = std::abs(N[2]);//Math.Abs(N.Z);
		float t;
		if (absZ > absX && absZ > absY) {
			t = (SR[0]*directionE[1] - SR[1]*directionE[0])/N[2];
		} else if (absX > absY) {
			t = (SR[1]*directionE[2] - SR[2]*directionE[1])/N[0];
		} else {
			t = (SR[2]*directionE[0] - SR[0]*directionE[2])/N[1];
		}
		return originD - t*directionD;
	}

