	void MeshGenerate::Circumcircle(const MyDenseVector& A,const MyDenseVector& B,const MyDenseVector& C,MyDenseVector& Center,MyFloat& radius)
	{
		// lines from a to b and a to c
		MyDenseVector AB = B - A;
		MyDenseVector AC = C - A;

		// perpendicular vector on triangle
		MyDenseVector N = (AB.cross(AC));
		N.normalize();
		//var N = Vector3.Normalize(Vector3.Cross(AB, AC));

		// find the points halfway on AB and AC
		MyDenseVector halfAB = A + AB*0.5f;
		MyDenseVector halfAC = A + AC*0.5f;

		// build vectors perpendicular to ab and ac
		MyDenseVector perpAB = AB.cross(N);//Vector3.Cross(AB, N);
		MyDenseVector perpAC = AC.cross(N);//Vector3.Cross(AC, N);

		// find intersection between the two lines
		// D: halfAB + t*perpAB
		// E: halfAC + s*perpAC
		Center = LineLineIntersection(halfAB, perpAB, halfAC, perpAC);
		// the radius is the distance between center and any point
		// distance(A, B) = length(A-B)

		radius = (Center - A).norm();//Vector3.Distance(center, A);
	}

