	void MeshGenerate::computeTriangleCenter()
	{
		const int nTriCount = m_nMaxFaceCount;
		for (unsigned t=0;t<nTriCount;++t)
		{
			MC_Surface& curTri = m_vec_MCSurface[t];
			MC_Vertex& p0 = m_vec_MCVertex[curTri.m_Vertex[0]];
			MC_Vertex& p1 = m_vec_MCVertex[curTri.m_Vertex[1]];
			MC_Vertex& p2 = m_vec_MCVertex[curTri.m_Vertex[2]];

			REAL x0 = p0.m_VertexPos[0];
			REAL y0 = p0.m_VertexPos[1];
			REAL z0 = p0.m_VertexPos[2];

			REAL x1 = p1.m_VertexPos[0];
			REAL y1 = p1.m_VertexPos[1];
			REAL z1 = p1.m_VertexPos[2];

			REAL x2 = p2.m_VertexPos[0];
			REAL y2 = p2.m_VertexPos[1];
			REAL z2 = p2.m_VertexPos[2];
 
			curTri.m_center[0] = (x0 + x1 + x2) / 3.f;
			curTri.m_center[1] = (y0 + y1 + y2) / 3.f;
			curTri.m_center[2] = (z0 + z1 + z2) / 3.f;
		}
	}

